/* Humburger */
window.addEventListener('DOMContentLoaded', () => {
    const menu = document.querySelector('.nav'),
    menuItem = document.querySelectorAll('.nav_item'),
    hamburger = document.querySelector('.hamburger');

    hamburger.addEventListener('click', () => {
        hamburger.classList.toggle('hamburger_active');
        menu.classList.toggle('nav_active');
    });

    menuItem.forEach(item => {
        item.addEventListener('click', () => {
            hamburger.classList.toggle('hamburger_active');
            menu.classList.toggle('nav_active');
        })
    })
})

$(document).ready(function(){
    
    /*------------------ Animate WOW ------------------*/
    var wow = new WOW({
        mobile: false
    })
    wow.init();
    
    //Tabs
  
      $('ul.work-tab').on('click', 'li:not(work-tab-link_active)', function() {
        $(this)
          .addClass('work-tab-link_active').siblings().removeClass('work-tab-link_active')
          .closest('div.work').find('div.work-content-tab').removeClass('work-content-tab_active').eq($(this).index()).addClass('work-content-tab_active');
      });

    //Smooth scroll and pageup

    $(window).scroll(function () {
        if ($(this).scrollTop() > 1000) {
            $('.up-btn').fadeIn();
        } else {
            $('.up-btn').fadeOut();
        }
        var scroll = $(window).scrollTop(),
        dh = $(document).height(),
        wh = $(window).height();
        scrollPercent = (scroll / (dh-wh))*100;
        $('.progressbar').css('height', scrollPercent + '%');
    });

   /*------------------ Scroll Menu------------------*/

   $(".nav-links-item, .scrollup").mPageScroll2id();

   //button

    $('.btn').on('mouseenter', function(e){
        x = e.pageX - $(this).offset().left;
        y = e.pageY - $(this).offset().top;
        $(this).find('span').css({top:y, left:x})
    });
    $('.btn').on('mouseout', function(e){
        x = e.pageX - $(this).offset().left;
        y = e.pageY - $(this).offset().top;
        $(this).find('span').css({top:y, left:x})
    });

   // Modal

    $('[data-modal=consultation]').on('click', function () {
        $('.overlay, #modal-form').fadeIn('slow');

    });
    $('.modal__close').on('click', function () {
        $('.overlay, #modal-form, #thanks').fadeOut('slow');
    });

   //masked input

    $('.input-phone').mask("+7 (999) 999-99-99");

    //validation form and php настройки

    function valideForms(form) {
        $(form).validate({
            rules: {
                name: {
                    required: true,
                    minlength: 2
                },
                
                phone: 'required',
                email: {
                    required: true,
                    email: true
                },
                textMessage: 'required'
            },
            messages: {
                name: {
                    required: "Пожалуйста, введите своё имя",
                    minlength: jQuery.validator.format("Введите {0} символа!")
                },
                phone: 'Пожалуйста, введите свой номер телефона',
                email: {
                    required: "Пожалуйста, введите свой почтовый адрес",
                    email: "Неправильно введён адрес почты"
                },
                textMessage: 'Пожалуйста, напишите свой вопрос'
            },
            submitHandler: function () {
                $('form').submit(function (e) {
                    e.preventDefault();
                    $.ajax({
                        type: 'POST',
                        url: "mailer/smart.php",
                        data: $(this).serialize()

                    }).done(function () {
                        $(this).find('input').val("");
                        $('#modal-form').fadeOut();
                        $('.overlay, #thanks').fadeIn('slow');

                        $('form').trigger("reset");
                    });
                    return false;
                });
            }
        });
    };
    valideForms('#consultation');
    valideForms('#modal-form form');
      
});
